import express from 'express';
import morgan from 'morgan';
import cors from 'cors';
import mongoose from 'mongoose';
import config from './config/config';
// Importar rutas
import routeAPI from './api/v1/routes/index';

const app = express();
app.set('port', config.PORT);
app.use(cors());
app.use(morgan('dev'));
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Conectar a la base de datos
mongoose.connect(config.CONNECTION_STRING, {
    useNewUrlParser: true,
    useUnifiedTopology: true,
}).then(() => {
    console.log('Database is connected to:', config.DATABASE);
}).catch(err => console.error('Database connection error:', err));

// Inicializar las rutas
routeAPI(app); // Llamar al método para inicializar las rutas

// Ruta raíz
app.get(`${config.API_URL}`, (req, res) => {
    res.send(`<h1>RESTful running in root</h1> <p> eCommerce: <b>${config.API_URL}/api-docs</b> for more information.</p>`);
});

// Exportar app
export default app;

