// Importar la clase de Servicios de Productos y Servicios
import * as ProdServServices from '../services/prodServ.service'; // Asegúrate de que la ruta sea correcta
import boom from '@hapi/boom';

// Definición de métodos API GET

//FIC: API GET
//----------------------------------------
//FIC: Todos los Productos/Servicios.
export const getProdServList = async (req, res, next) => {
    try {
        const prodServList = await ProdServServices.getProdServList();
        if (!prodServList || prodServList.length === 0) {
            throw boom.notFound('No se encontraron productos/servicios registrados.');
        } else {
            res.status(200).json(prodServList);
        }
    } catch (error) {
        next(error);
    }
};

//FIC: Solo un Producto/Servicio.
export const getProdServItem = async (req, res, next) => {
    try {
        // Obtener parámetro id mediante la desestructuración de objetos
        const { id } = req.params;
        const keyType = req.query.keyType || 'OK'; // Por defecto, 'OK' si no se proporciona

        const prodServItem = await ProdServServices.getProdServItem(id, keyType);
        if (!prodServItem) {
            throw boom.notFound('No se encontró el producto/servicio registrado.');
        } else {
            res.status(200).json(prodServItem);
        }
    } catch (error) {
        next(error);
    }
};

