// Importar la clase modelo de Productos y Servicios
import ProdServ from '../models/ProdServ';
import boom from '@hapi/boom';

// Método para obtener todos los productos y servicios
export const getProdServList = async () => {
    let prodServList;
    try {
        // Obtener todos los registros de la colección "cat_prod_serv"
        prodServList = await ProdServ.find();
        return prodServList;
    } catch (error) {
        // Manejo de errores
        throw boom.internal(error);
    }
};

// Método para obtener un producto o servicio por ID
export const getProdServItem = async (id, keyType) => {
    let prodServItem;

    try {
        // Validar el campo por el cual se realizará la consulta
        if (keyType === 'OK') {
            prodServItem = await ProdServ.findOne({
                IdProdServOK: id,
            });
        } else if (keyType === 'BK') {
            prodServItem = await ProdServ.findOne({
                IdProdServBK: id,
            });
        }
        return prodServItem;
    } catch (error) {
        // Manejo de errores
        throw boom.internal(error);
    }
};

