sourcesContent:
const mongoose = require('mongoose');

// Definir el esquema de precios
const precioSchema = new mongoose.Schema({
    IdProdServOK: { type: String },
    IdPresentaOK: { type: String },
    IdTipoFormulaOK: { type: String },
    Formula: { type: String },
    CostoIni: { type: Number },
    CostoFin: { type: Number },
    Precio: { type: Number },
    detail_row: {
        Activo: { type: String, default: 'S' },
        Borrado: { type: String, default: 'N' },
        detail_row_reg: [
            {
                FechaReg: { type: Date, default: Date.now },
                UsuarioReg: { type: String }
            }
        ]
    }
}, { _id: false });

// Definir el esquema principal para la colección
const listaSchema = new mongoose.Schema({
    IdInstitutoOK: { type: String, required: true },
    IdListaOK: { type: String, required: true },
    IdListaBK: { type: String },
    DesLista: { type: String },
    FechaExpiraIni: { type: Date },
    FechaExpiraFin: { type: Date },
    IdTipoListaOK: { type: String },
    IdTipoGeneraListaOK: { type: String },
    IdTipoFormulaOK: { type: String },
    precios: [precioSchema],
    roles: [{ type: mongoose.Schema.Types.Mixed }],
    negocios: [{ type: mongoose.Schema.Types.Mixed }],
    promociones: [{ type: mongoose.Schema.Types.Mixed }],
    detail_row: {
        Activo: { type: String, default: 'S' },
        Borrado: { type: String, default: 'N' },
        detail_row_reg: [
            {
                FechaReg: { type: Date, default: Date.now },
                UsuarioReg: { type: String }
            }
        ]
    }
});

module.exports = mongoose.model('cat_precios',   listaSchema, 'cat_precios');

