"use strict";

var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// Definir el esquema de precios
var detailRowRegSchema = new Schema({
  FechaReg: {
    type: Date,
    required: true
  },
  UsuarioReg: {
    type: String,
    required: true
  }
});
var detailRowSchema = new Schema({
  Activo: {
    type: String,
    required: true
  },
  Borrado: {
    type: String,
    required: true
  },
  detail_row_reg: [detailRowRegSchema]
});
var precioSchema = new Schema({
  IdProdServOK: {
    type: String,
    required: true
  },
  IdPresentaOK: {
    type: String,
    required: true
  },
  IdTipoFormulaOK: {
    type: String,
    "default": ''
  },
  Formula: {
    type: String,
    "default": ''
  },
  CostoIni: {
    type: Number,
    required: true
  },
  CostoFin: {
    type: Number,
    required: true
  },
  Precio: {
    type: Number,
    required: true
  },
  detail_row: detailRowSchema
});
var listaPreciosSchema = new Schema({
