    type: String,
    required: true
  },
  IdTipoGeneraListaOK: {
    type: String,
    required: true
  },
  IdTipoFormulaOK: {
    type: String,
    "default": ''
  },
  precios: [precioSchema],
  detail_row: detailRowSchema,
  roles: {
    type: Array,
    "default": []
  },
  negocios: {
    type: Array,
    "default": []
  },
  promociones: [promocionSchema] // Cambiado para usar el nuevo esquema
});

// Exportar el modelo
module.exports = mongoose.model('cat_precios', listaPreciosSchema, 'cat_precios');
